import React, {useState} from 'react'
import Persons from './Persons'



const FilterForm = (props) => {
const [ nameFilter, setNameFilter] = useState('')


const filterNames = (arr, arg) => {
    return arr.filter(person => person.name.toLowerCase().indexOf(arg.toLowerCase()) !== -1)
  }

const FilteredPersons = (props) => {
    return (
        <ul>
        {filterNames(props.persons, props.nameFilter).map((person, name) => <Person key={name} person={person} />)}
        </ul>)
}

const handleFilterChange = (event) => {
    setNameFilter(event.target.value)
}
    return (
        <div>filter shown with  
          <input  
                  type="text"
                  placeholder="Search name..."
                  value={nameFilter}
                  onChange={handleFilterChange} />
        </div>
    )
}


export default FilterForm
