import React, { useState } from 'react'
import Persons from './components/Persons'

/*const Person = (props) => {
  return (
  <li>{props.person.name} {props.person.number}</li>
  )
}

const filterNames = (arr, arg) => {
  return arr.filter(person => person.name.toLowerCase().indexOf(arg.toLowerCase()) !== -1)
}*/

const App = (props) => {

  const [ persons, setPersons] = useState(props.persons) 
  const [ newName, setNewName ] = useState('')
  const [ nameFilter, setNameFilter] = useState('')
  const [ newPhoneNumber, setNewPhoneNumber ] = useState('')

  const addPerson = (event) => {
    event.preventDefault()

    const personObject ={
      name: newName,
      number: newPhoneNumber
    }    

    persons.map(person => person.name).includes(newName, 0)

        ? window.alert(`${newName} is already added to phonebook`)

        : setPersons(persons.concat(personObject))


    setNewName('')
    setNewPhoneNumber('')
  }

  const handleNameAdd = (event) => {
    setNewName(event.target.value)
  }




  const handlePhoneNumberAdd =(event) => {
    setNewPhoneNumber(event.target.value)
  }







  return (
    <div>
      <h2>Phonebook</h2>

        <div>filter shown with  
          <input  
                  type="text"
                  placeholder="Search name..."
                  value={nameFilter}
                  onChange={handleFilterChange} />
        </div>
      <h3>add a new</h3>
      <form onSubmit={addPerson}>
          <div>
            name: <input 
              value={newName}
              onChange={handleNameAdd} />
          </div>
          <div>
            number: <input 
              value={newPhoneNumber}
              onChange={handlePhoneNumberAdd} />
          </div>
          <div>
            <button type="submit">add</button>
          </div>
      </form>
      <h2>Numbers</h2>
      <ul>
        <Persons persons={persons} nameFilter={nameFilter}/>
      </ul>
    </div>
  )

}

export default App
