import React from 'react'
// import filterNames  from './FilterForm'

const Person = ({ name, number, deletePerson }) => {
    return (
        <li>
            {name} {number}
            <button onClick={deletePerson}>delete</button>
        </li>)
}

const FilteredPersons = ({ persons, nameFilter, deletePerson}) => {
    return (
        <ul>
        {filterNames(persons, nameFilter).map((person, name) => {
            return <Person key={name} name={person.name} number={person.number} deletePerson={deletePerson} />
        })}
        </ul>)
}



const filterNames = (arr, arg) => arr.filter(person => person.name.toLowerCase().indexOf(arg.toLowerCase()) !== -1)

const Persons = ({ persons, nameFilter, deletePerson}) => {
    return <FilteredPersons persons={persons} nameFilter={nameFilter} />
}

export default Persons
