import React, { useState } from 'react'

const App = (props) => {
  const [ persons, setPersons] = useState(props.persons) 
  const [ newName, setNewName ] = useState('')

  const [ newPhoneNumber, setNewPhoneNumber] = useState('')

  const addPerson = (event) => {
    event.preventDefault()

    const personObject ={
      name: newName,
      number: newPhoneNumber
    }

    persons.map(person => person.name).includes(newName, 0)
    ? window.alert(`${newName} is already added to phonebook`)
    : setPersons(persons.concat(personObject)) && handleFilterChange()

    setNewName('')
    setNewPhoneNumber('')
  }

  const handleNameChange = (event) => {
    setNewName(event.target.value)
  }

  const Person = (props) => {
    return (
    <li>{props.person.name} {props.person.number}</li>
    )
  }

  const handlePhoneNumberChange =(event) => {
    setNewPhoneNumber(event.target.value)
  }

  const [filteredPersons, setNameFilter] = useState([...persons])

  const filterNames = (arr, arg) => {
    return arr.filter(person => person.name.toLowerCase().indexOf(arg.toLowerCase()) !== -1)
  }

  const handleFilterChange = (event) => {
    setNameFilter(filterNames(persons, event.target.value))
    //filteredPersons = persons.filter(person => person.name.toLowerCase().indexOf(nameFilter.toLowerCase()))

  }

  return (
    <div>
      <h2>Phonebook</h2>
      <div>filter shown with <input onChange={handleFilterChange}/></div>
      <h3>add a new</h3>
      <form onSubmit={addPerson}>
        <div>
          name: <input 
            value={newName}
            onChange={handleNameChange}
          />
        </div>
        <div>
          number: <input 
             value={newPhoneNumber}
             onChange={handlePhoneNumberChange}
          />
        </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>
      <ul>
        {filteredPersons.map((person, name) => {
          //console.log(person)
          return <Person key={name} person={person} />
        })}
      </ul>
    </div>
  )

}

export default App
