                    ? persons.map((person, name) => {
                          console.log('filtteröimätön')
                          return <Person key={name} person={person} /> 
                      })

                    : filterNames( persons, nameFilter).map((person, name) => {
                          console.log('filtteröity')
                          return <Person key={name} person={person} />
