import React from 'react'
// import filterNames  from './FilterForm'

const Person = (props) => {
    return (<li>{props.person.name} {props.person.number}</li>)
}

const FilteredPersons = (props) => {
    console.log(props.nameFilter)

    return (
        <ul>
        {filterNames(props.persons, props.nameFilter).map((person, name) => <Person key={name} person={person} />)}
        </ul>)
}

const filterNames = (arr, arg) => {
    console.log(arr, arg)
    return arr.filter(person => person.name.toLowerCase().indexOf(arg.toLowerCase()) !== -1)
  }

