import React from 'react'

const Person = (props) => {
    console.log(props.person)
    return (<li>{props.person.name} {props.person.number}</li>)
}

const FilteredPersons = (persons, nameFilter) => {
    console.log(persons, nameFilter)

    return (
        <ul>
        {filterNames(persons, nameFilter).map((person, name) => <Person key={name} person={person} />)}
        </ul>)
}

const filterNames = (arr, arg) => {
    console.log(arg)
    return arr.filter(person => person.name.toLowerCase().indexOf(arg.toLowerCase()) !== -1)
}


const Persons = (props) => {
    const persons = [...props.persons]
    const nameFilter = props.nameFilter

    return <FilteredPersons persons={persons} nameFilter={nameFilter}/>

