import React, { useState, useEffect } from 'react'
import personsService from './services/persons'

import Persons from './components/Persons'
import FilterForm from './components/FilterForm'
import PersonForm from './components/PersonForm'


const App = () => {
  const [ persons, setPersons] = useState([]) 
  const [ newName, setNewName ] = useState('')
  const [ newPhoneNumber, setNewPhoneNumber ] = useState('')
  const [ nameFilter, setNameFilter] = useState('')

  const handleFilterChange = (event) => {
      setNameFilter(event.target.value)
  }

  useEffect(() => {
    personsService
      .getAll()
      .then(initialPersons => {
        setPersons(initialPersons)
      }) 
    }, [])

  const addPerson = (event) => {
    event.preventDefault()

    const personObject ={
      name: newName,
      number: newPhoneNumber
    }    

    persons.map(person => person.name).includes(newName, 0)

        ? window.alert(`${newName} is already added to phonebook`)

        : personsService
            .create(personObject)
            .then(returnedPerson => {
              setPersons(persons.concat(returnedPerson))
              setNewName('')
              setNewPhoneNumber('')
            })



  }

  const handleNameAdd = (event) => {
    setNewName(event.target.value)
  }


  const handlePhoneNumberAdd =(event) => {
    setNewPhoneNumber(event.target.value)
  }

  const deletePerson = ({ name, id }) => {
    if (window.confirm(`Delete ${name}?`)) {
      personsService
            .deletePerson(id)
            .then(initialPersons => {
              setPersons(initialPersons)
            })
    }

  }

  return (
    <div>
      <h2>Phonebook</h2>
      <FilterForm onChange={handleFilterChange} nameFilter={nameFilter}/>

      <h3>add a new</h3>
      <PersonForm onSubmit={addPerson} name={newName} onNameChange={handleNameAdd} phoneNumber={newPhoneNumber} onNumberChange={handlePhoneNumberAdd} />
      <h2>Numbers</h2>
      <ul>
        <Persons
            persons={persons}
            nameFilter={nameFilter}
            deletePerson={deletePerson} />
      </ul>
    </div>
  )

}

export default App
